import React,{Component} from 'react';
import ReactEcharts from 'echarts-for-react';

/**
 * @author hui
 * @date 2019/4/18
 * @Description: DemoChart
 *
*/
export default class DemoChart extends Component {

	constructor(props) {
		super(props);

		this.demoEchat = {};
	}

	componentWillMount(){
        let data = {
            "chart": [{
                month: "1月",
                value: 138,
                ratio: 14.89
            },

                {
                    month: "2月",
                    value: 114,
                    ratio: 79.49
                },

                {
                    month: "3月",
                    value: 714,
                    ratio: 75.8
                },

                {
                    month: "4月",
                    value: 442,
                    ratio: 19.8
                },

                {
                    month: "5月",
                    value: 622,
                    ratio: 44.5
                },


                {
                    month: "6月",
                    value: 528,
                    ratio: 87.3
                }

            ]
        }


        let xAxisMonth = [],
            barData = [],
            lineData = [];
        for (let i = 0; i < data.chart.length; i++) {
            xAxisMonth.push(data.chart[i].month);
            barData.push({
                "name": xAxisMonth[i],
                "value": data.chart[i].value
            });
            lineData.push({
                "name": xAxisMonth[i],
                "value": data.chart[i].ratio
            });
        }

	    this.demoEchat = {
            backgroundColor: "#fff",
            title: '',
            grid: {
                top: '24%',
                left: '7%',
                bottom: '6%',
                containLabel: true
            },
            tooltip: {
                trigger: 'axis',
                axisPointer: {
                    type: 'none'
                },
                formatter: function(params) {
                    return params[0]["data"].name + "<br/>" + '喜欢人数: ' + params[1]["data"].value + "<br/>" + '不喜欢人数: ' + params[0]["data"].value;
                }
            },
            xAxis: [
                {
                    type: 'category',
                    show: false,
                    data: ['3月', '4月', '5月', '6月', '7月', '8月'],
                    axisLabel: {
                        textStyle: {
                            color: '#b6b5ab'
                        }
                    }
                },
                {
                    type: 'category',
                    position: "bottom",
                    data: xAxisMonth,
                    boundaryGap: true,
                    // offset: 40,
                    axisTick: {
                        show: false
                    },
                    axisLine: {
                        show: false
                    },
                    axisLabel: {
                        textStyle: {
                            color: '#b6b5ab'
                        }
                    }
                }

            ],
            yAxis: [
                {
                    show: false,
                    offset: 52,
                    splitLine: {
                        show: false,
                        lineStyle: {
                            color: "rgba(255,255,255,0.2)"
                        }
                    },
                    axisTick: {
                        show: false
                    },
                    axisLine: {
                        show: true
                    },
                    axisLabel: {
                        show: true,
                        color: '#b6b5ab'
                    }
                }, {
                    show: false,
                    type: "value",
                    name: "合格率(%)",
                    nameTextStyle: {
                        color: '#ccc'
                    },
                    axisLabel: {
                        color: '#ccc'
                    },
                    splitLine: {
                        show: false
                    },
                    axisLine: {
                        show: false
                    },
                    axisTick: {
                        show: false
                    }
                }
            ],
            color: ['#e54035'],
            series: [{
                name: '喜欢人数',
                type: 'pictorialBar',
                xAxisIndex: 1,
                barCategoryGap: '-80%',
                // barCategoryGap: '-5%',
                symbol: 'path://d="M150 50 L130 130 L170 130  Z"',
                itemStyle: {
                    normal: {
                        color: function(params) {
                            let colorList = [
                                'rgba(13,177,205,0.8)', 'rgba(29,103,182,0.6)',
                                'rgba(13,177,205,0.8)', 'rgba(29,103,182,0.6)',
                                'rgba(13,177,205,0.8)', 'rgba(29,103,182,0.6)'
                            ];
                            return colorList[params.dataIndex];
                        }
                    },
                    emphasis: {
                        opacity: 1
                    }
                },
                data: barData,
            },
                {
                    symbol: 'image://data:image/png;base64,',
                    symbolSize: 42,
                    name: "不喜欢人数",
                    type: "line",
                    yAxisIndex: 1,
                    data: lineData,
                    itemStyle: {
                        normal: {
                            borderWidth: 5,
                            color: {
                                colorStops: [{
                                    offset: 0,
                                    color: '#821eff'
                                },

                                    {
                                        offset: 1,
                                        color: '#204fff'
                                    }
                                ],
                            }
                        }
                    }
                }
            ]
        }
    }

	render() {
		return (
            <div style={{width:'100%',clear:'both',margin:'0 auto'}}>
                <ReactEcharts
                    option={this.demoEchat}
                    style={{width: '100%'}}
                />
            </div>
			)
	}
}
